mapper.update:
UPDATE MAIL_FILE
SET M_STATUS = 'N'
WHERE M_FILE_NO=#{mFileNo}

update.if:
update mail_s_r
set s_r_status='N'
where mailno=#{mNo}
and empno=#{empNo}

delete from mail
where mailno=#{mNo}
and empno=#{empNo}
and estatus='T'

mapper.update:

update mail_s_r
set r_date = sysdate
where mailno = #{id}	
and empno = #{empNo}

UPDATE MAIL
SET
	RECEIVEEMP = #{receiveEmp},
	etitle = #{etitle},
	econtent = #{econtent}
where mailNo = #{mailNo}

UPDATE MAIL_S_R
SET favorites = #{favorites}
where empno = #{empNo}
and mailno = #{id}
